mapper.insert:
insert into member(
	member_id, member_pw, member_nickname,
	member_email, member_birth, member_contact,
	member_post, member_address1, member_address2
)
values(
	#{memberId},#{memberPw},#{memberNickname},
	#{memberEmail}, #{memberBirth},#{memberContact},
	#{memberPost}, #{memberAddress1}, #{memberAddress2}
)

mapper.update:
update member
set member_pw=#{memberPw}
where member_id=#{memberId}

update member
set member_nickname=#{memberNickname}
where member_id=#{memberId}

update member
set member_point=#{memberPoint}
where member_id=#{memberId}

update member
set member_reliability=#{memberReliability}
where member_id=#{memberId}

update member
set member_level = #{memberLevel}
where member_id = #{memberId}

mapper.select:
SELECT M.member_id AS "memberId", M.member_nick AS "nickname", M.member_point AS "point", M.member_level AS "level", M.member_nick_style AS "nickStyle",
-- ⭐ 장착 중 아이콘 이미지 경로 (SELECT I.icon_src FROM MEMBER_ICONS MI JOIN ICON I ON MI.member_icon_icon = I.icon_id WHERE MI.member_icon_member = M.member_id AND MI.is_equipped = 'Y' FETCH FIRST 1 ROWS ONLY) AS "iconSrc"
FROM MEMBER M WHERE M.member_id = #{memberId}

mapper.update:
UPDATE member
SET member_point = member_point + #{memberPoint}
WHERE member_id = #{memberId}
